from django.contrib import adminfrom django.db.models import Countfrom ..models import PythonTipHashTag@admin.register(PythonTipHashTag)class PythonTipHashTagAdmin(admin.ModelAdmin):    list_display = [        'name',        'tweets_with_tag'    ]    @admin.display(description='Num of tweets with tag')    def tweets_with_tag(self, tags):        return tags.count_tweets_with_tag    def get_queryset(self, request):        qs = super().get_queryset(request)        return qs.annotate(count_tweets_with_tag=Count('tips'))__all__ = [    'PythonTipHashTagAdmin']