from django.contrib import adminfrom ..models import PythonTip@admin.register(PythonTip)class PythonTipAdmin(admin.ModelAdmin):    readonly_fields = [        'tweet_id',    ]    list_display = [        'tweet_id',        'likes',        'retweets',        'get_hash_tags',        'published',        'timestamp'    ]    list_filter = [        'published',        'tweeted_by'    ]    search_fields = [        'tweet_id',        'hash_tags__name',        'tweeted_by',        'tweet'    ]    ordering = [        'published',        'retweets',        'likes',        'timestamp'    ]    date_hierarchy = 'timestamp'    @admin.display(description='Hash Tags', empty_value='No tags')    def get_hash_tags(self, tips):        tags = tips.hash_tags.all()        return ', '.join(tag.name for tag in tags) if tags else 'No tags yet'__all__ = [    'PythonTipAdmin']