from django.db import modelsfrom django.utils.translation import gettext_lazy as _class PythonTipHashTag(models.Model):    name = models.CharField(        verbose_name=_('#HashTag'),        max_length=50,        unique=True    )    slug = models.SlugField(        verbose_name=_('Slug'),        unique=True,    )    def __str__(self):        return f'#{self.name}'    def save(self, *args, **kwargs):        self.name = self.name.lower()        super().save(*args, **kwargs)    class Meta:        ordering = [            'name'        ]        verbose_name = _('Hash Tag')