from django.db import modelsfrom django.utils.translation import gettext_lazy as _class PythonTipHashTag(models.Model):    name = models.CharField(        verbose_name=_('Name'),        max_length=30,        unique=True,        db_index=True    )    def __str__(self):        return f'#{self.name}'    def save(self, *args, **kwargs):        self.name = self.name.lower()        super().save(*args, **kwargs)    class Meta:        ordering = [            'name'        ]        verbose_name = _('Hash Tag')