from django.db import modelsfrom django.utils.translation import gettext_lazy as _class PythonTipLink(models.Model):    tip = models.ForeignKey(        'twee.PythonTip',        verbose_name=_('Tip'),        on_delete=models.CASCADE,        related_name='links'    )    url = models.URLField()    class Meta:        constraints = [            models.UniqueConstraint(fields=['tip', 'url'], name='unique_links')        ]        verbose_name = _('Hyperlink')    def __str__(self):        return f'{self.url} on {self.tip}'