from django.db import modelsfrom django.utils.translation import gettext_lazy as _from django_cryptography.fields import encryptclass TweeCrypt(models.Model):    consumer_key = encrypt(models.CharField(        verbose_name=_('Twitter API Consumer Key'),        max_length=25,    ))    consumer_secret = encrypt(models.CharField(        verbose_name=_('Twitter API Consumer Secret'),        max_length=50    ))    access_token = encrypt(models.TextField(        verbose_name=_('Twitter API Consumer Key'),        max_length=50    ))    access_token_secret = encrypt(models.TextField(        verbose_name=_('Twitter API Access Token Secret'),        max_length=50    ))    def save(self, *args, **kwargs):        self.id = 1        super().save(*args, **kwargs)    class Meta:        verbose_name = _('Twitter API Config Key')