from django.db.models.signals import pre_savefrom django.dispatch import receiverfrom ..models import PythonTipHashTag@receiver(pre_save, sender=PythonTipHashTag)def prevent_two_similar_tags(sender, instance, **kwargs):    first_tag = PythonTipHashTag.objects.filter(name=instance.name).first()    if first_tag:        instance.id = first_tag.id        instance.name = first_tag.name__all__ = [    'prevent_two_similar_tags']